import React, { useState } from 'react'
import hoodie from '../hoodie.png'
export const ProductCard = () => {
  const [checkedOne,  setCheckedOne] = useState(false)
  const [checkedTwo,  setCheckedTwo] = useState(false)
  const [checkedtri,  setCheckedTri] = useState(false)

  const product_mock = {
    name: '',
    size: ['130','140','150'],
    price: 222,
  }
  const handleOne = () => {
    setCheckedOne(true)
    setCheckedTwo(false)
    setCheckedTri(false)
  }

  const a = '50000'
  return (
    <div className='flex flex-col w-[360px] h-[450px] bg-bgColor rounded-[20px] overflow-hidden justify-between '>
      <img alt='' src={hoodie} className='h-[900px]'/> 
      <div className='p-3 flex flex-col h-full justify-between'>
        <p className='font-[Shantell] text-start w-full '>Name</p>
        <div className=' w-full row-span-3 gap-x-3 flex '>
          <button className={`!checkedOne ? bg-purple text-black font-[Shantell] p-1 rounded-[10px] : hover:bg-purple text-black font-[Shantell] p-1 rounded-[10px]`} onClick={handleOne}>130</button>
          <button className='hover:bg-purple text-black font-[Shantell] p-1 rounded-[10px]'>140</button>
          <button className='hover:bg-purple text-black font-[Shantell] p-1 rounded-[10px]'>150</button>
        </div>
        <p className='font-[Shantell]'>Үнэ : {a}₮</p>
        <button className='bg-pink font-[Shantell] w-full rounded-[10px] hover:bg-bgColor border-solid border-pink border-2' >Сагслах</button>
      </div>
    </div>
  )
}
